import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import '../css/LoginFormcss.css'
import swal from 'sweetalert';
import axios from 'axios';

function getCookie(name) {
  const value = `; ${document.cookie}`;
  const parts = value.split(`; ${name}=`);
  if (parts.length === 2) {
    return parts.pop().split(';').shift();
  }
}

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [setError] = useState(null);

  useEffect(() => {
    checkCookies();
  }, []);

  const checkCookies = async () => {
    const token = getCookie('token'); // this function should retrieve the token from the cookies


    if (token) {
      try {
        const response = await axios.post('http://localhost:8080/protected', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (response.data.status === 'success') {
          window.location.href = '/Home';
        }
      } catch (e) {
        // handle error
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8080/login', {
        username,
        password,
      });

      if (response.data.status === 'success') {
        swal("Success", response.message, "success", {
          buttons: false,
          timer: 2000,
        })
            } else {
        setError(response.data.message);
      }
    } catch (e) {
      swal("Failed", document.response.message, "error");
    }

    swal("Failed", document.response.message, "error");

  };


  return (
    <div className='wrapper d-flex align-items-center justify-content-center w-100'>
      <div className='login'>
      <h2 className='mb-3'>Login</h2>
      <Form noValidate  onSubmit={handleSubmit}>
        <Row className="mb-3">
          <Form.Group noValidate  onSubmit={handleSubmit} as={Col} md="12" controlId="validationCustom01">
            <Form.Label>Account</Form.Label>
            <Form.Control
              required
              type="text"
              placeholder="Username"
              onChange={e => {setUsername(e.target.value);
                }
              }
            />
            <Form.Control.Feedback>
              Looks good!
            </Form.Control.Feedback>
            <Form.Control.Feedback type="invalid">
              Username cannot be empty!
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group as={Col} md="12" controlId="validationCustom02">
            <Form.Label>Password</Form.Label>
            <Form.Control
              required
              type="password"
              placeholder="Password"
              onChange={e => setPassword(e.target.value)}
            />
            <Form.Control.Feedback>
              Looks good!
            </Form.Control.Feedback>
            <Form.Control.Feedback type="invalid">
              Password cannot be empty!
            </Form.Control.Feedback>
          </Form.Group>
        </Row>
        <Form.Group className="mb-3">
          <Form.Check
            required
            label="Agree to terms and conditions"
            feedback="You must agree before submitting."
            feedbackType="invalid"
          />
        </Form.Group>
        <Button  className="test" type="submit">Login </Button>
      </Form>
      </div>
    </div>
  );
}

export default LoginForm
